div.script:
               function setvalues(){
                   try{
                       $("input[name='name']").val($("#sol_name").html());
                       $("input[name='nombre_comercial']").val($("#sol_nombre_comercial").html());
                       $("input[name='representante_legal']").val($("#sol_representante_legal").html());
                       $("input[name='empresa_nit']").val($("#sol_empresa_nit").html());
                       $("input[name='representante_nit']").val($("#sol_representante_nit").html());
                       $("input[name='representante_dui']").val($("#sol_representante_dui").html());
                       $("input[name='apoderado']").val($("#sol_apoderado").html());
                       $("input[name='apoderado_nit']").val($("#sol_apoderado_nit").html());
                       $("input[name='apoderado_dui']").val($("#sol_apoderado_dui").html());

                       $("input[name='poder_filename']").val($("#sol_poder_filename").html());
                       $("#departamento_id").val($("#sol_departamento_id").html());

                       updatemunicipio();
                       $("input[name='municipio_id']").val($("#sol_municipio_id").html());
                       $("#representante_nacionalidad").val($("#sol_representante_nacionalidad").html());
                       $("input[name='representante_profesion']").val($("#sol_representante_profesion").html());
                       $("input[name='representante_nacimeinto']").val($("#sol_representante_nacimeinto").html());
                       $("input[name='contacto']").val($("#sol_contacto").html());
                       $("input[name='contacto_email']").val($("#sol_contacto_email").html());
                       $("input[name='usuario_id']").val($("#sol_usuario_id").html());
                       $("input[name='tipo']").val($("#sol_tipo").html());
                       $("input[name='proyecto_name']").val($("#sol_proyecto_name").html());
                       $("input[name='proyecto_descripcion']").val($("#sol_proyecto_descripcion").html());
                       $("input[name='proyecto_resolucion']").val($("#sol_proyecto_resolucion").html());
                       $("input[name='proyecto_resolucion_nfa']").val($("#sol_proyecto_resolucion_nfa").html());
                       $("input[name='proyecto_resolucion_fecha']").val($("#sol_proyecto_resolucion_fecha").html());
                       $("input[name='proyecto_direccion']").val($("#sol_proyecto_direccion").html());
                       $("#proyecto_departamento_id").val($("#sol_proyecto_departamento_id").html());
                       updatemunicipio2();
                       $("input[name='proyecto_municipio_id']").val($("#sol_proyecto_municipio_id").html());
                       $("input[name='proyecto_afectacion']").val($("#sol_proyecto_afectacion").html());
                       $("input[name='proyecto_vencimiento']").val($("#sol_proyecto_vencimiento").html());
                       $("input[name='proyecto_valor']").val($("#sol_proyecto_valor").html().replace(',',''));
                       $("input[name='proceso_uuid']").val($("#sol_proceso_uuid").html());
                   }catch(err){

                   }
               }

               function update_departamento() {
                     $.ajax({url: "/fiaes/select/departamentos?field=departamento_id&label=Departamento%20de%20la%20empresa", success: function(result){
                         $("#departamento_container").html(result);

                         }});
               }

               function update_country() {
                     $.ajax({url: "/fiaes/select/countries", success: function(result){
                         $("#country_container").html(result);
                         }});
               }

               function updatemunicipio() {
                     $.ajax({url: "/fiaes/select/municipios?field=municipio_id&label=Municipio%20de%20la%20empresa&departamentoid=" + $("#departamento_id").val(), success: function(result){
                         $("#municipio_container").html(result);

                     }});
               }

               function update_departamento2() {
                     $.ajax({url: "/fiaes/select/departamentos?field=proyecto_departamento_id&label=Departamento%20del%20proyecto", success: function(result){
                         $("#departamentoproyecto_container").html(result);
                         }});
               }

               function updatemunicipio2() {                              
                     $.ajax({url: "/fiaes/select/municipios?field=proyecto_municipio_id&label=Departamento%20del%20proyecto&departamentoid=" + $("#proyecto_departamento_id").val(), success: function(result){
                         $("#municipioproyecto_container").html(result);
                     }});
               }


             $(document).ready(function() {
               update_country();
               update_departamento();
               update_departamento2();
               setTimeout('setvalues();', 5000);

               $(document).on('change',"#departamento_id", function(){
                     updatemunicipio();
               });

               $(document).on('change',"#tipo", function(){
                     $('.fiaes-persona').css( "display", "none" );
                     $('.fiaes-sociedad').css( "display", "none" );
                     $('.fiaes-ong').css( "display", "none" );
                     $('.fiaes-gobierno').css( "display", "none" );
                     if ($("#tipo").val()=='Sociedad'){
                         $('.fiaes-sociedad').css( "display", "flex" );
                     }
                     if ($("#tipo").val()=='Persona'){
                         $('.fiaes-persona').css( "display", "flex" );
                     }
                     if ($("#tipo").val()=='ONG'){
                         $('.fiaes-ong').css( "display", "flex" );
                     }
                     if ($("#tipo").val()=='Gobierno'){
                         $('.fiaes-gobierno').css( "display", "flex" );
                     }
               });


               $(document).on('change',"#proyecto_departamento_id", function(){
                     updatemunicipio2();
               });

               function marcar() {
                   document.getElementById("procesar").checked = true;
               }


});

form.script:
function sendValoresObservar(){
    $("input[name='proceso_id']").val($("#val_proceso_id").html());
    $("input[name='state']").val('Plan');
}
function sendValoresAprobar(){
    $("input[name='proceso_id']").val($("#val_proceso_id").html());
    $("input[name='state']").val('PlanAprobadoTitular');
}
function sendValoresObservarConvenio(){
    $("input[name='proceso_id']").val($("#val_proceso_id").html());
    $("input[name='state']").val('Plan');
}
function sendValoresAprobarConvenio(){
    $("input[name='proceso_id']").val($("#val_proceso_id").html());
    $("input[name='state']").val('ConvenioAprobadoTitular');
}
